package com.defoult_Value;

public class Casting_example {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte b=127;
		int i=b;
		System.out.println(i);//implicite casting

		
		float floatvalue=(float) 3.14;
		long longvalue=(long)floatvalue;   //expicit casting
		System.out.println(floatvalue);
		

	}

}
